<?php

class grid_ui_renderer
{
    private $gameName = 'DINOSAURS DIGGER';
    private $tilePixelSize = 75;

    public function __construct($tilePixelSize = 75){
        $this->tilePixelSize = $tilePixelSize;
    }

    public function gameUI(dig_field $field){
        echo '<h1 style="text-align: center; ">' . $this->gameName . '</h1>';
        $this->renderDigField($field);
    }

    private function renderDigField(dig_field $field){
        $maxStrength = $field->getMaxCellStrength();
        $images = glob('Images/' . '*.{jpg,jpeg,png,gif}', GLOB_BRACE);
        echo '<h2 style="text-align: center">DIG FIELD</h2>';
        echo '<table background="data:image/jpeg;base64,"
         style="margin-left: auto; margin-right: auto; border: 1px solid black; border-collapse: collapse">';
        for ($row = 0; $row < $field->getRowsCount(); $row++) {
            echo '<tr>';
            for ($col = 0; $col < $field->getColumnsCount(); $col++) {
                $tile = $field->getCell($col, $row);
                $breakingProgress = $tile/$maxStrength / (1/count($images));
                $breakingProgress = $breakingProgress > 0 && $breakingProgress < 1 ? 1 : $breakingProgress;
                $imageIndex = (int)($breakingProgress) - 1;
                $tileImage = $imageIndex >= 0 && $imageIndex < count($images) ?
                    $images[$imageIndex] : "https://upload.wikimedia.org/wikipedia/commons/5/59/Empty.png?20091205084734";
                echo '<td>';
                echo '<a href="index.php?row=' . $row . '&col=' . $col . '">
                        <img src="' . $tileImage . '" style="width: ' . $this->tilePixelSize .'px; height: ' . $this->tilePixelSize .'px">
                        </a>';
//                echo $tile;
                echo '</td>';
            }
            echo '</tr>';
        }
        echo '</table>';
    }

    private function RenderEmptyLine(){
        echo '<br/>';
    }
}